    private void btnProcesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProcesActionPerformed
        int ano,calc;
        String nome;
        nome = txtnome.getText();
        ano = Integer.parseInt(txtAno.getText());
        calc = 2021 - ano;
        if (calc < 18){
            lblResu.setText(nome + " NÃ£o pode tirar a CNH");
        }else {
            lblResu.setText(nome + " Pode tirar a CNH");
        }
        
        
    }//GEN-LAST:event_btnProcesActionPerformed

